mapper.select:
SELECT DISTINCT t1.ID_ AS processInstanceId, t1.PROC_DEF_ID_ as processDefinitionId, t1.NAME_ AS formName, -- t1.TENANT_ID_ AS systemSn, t1.BUSINESS_KEY_ AS businessKey, t1.START_TIME_ AS startTime, t1.END_TIME_ AS endTime, t2.ID_ AS starterId, t2.FIRST_ as starter, t3.SUSPENSION_STATE_ as suspensionState FROM ACT_HI_PROCINST t1 LEFT JOIN ACT_ID_USER t2 ON t1.START_USER_ID_ = t2.ID_ LEFT JOIN ACT_RU_EXECUTION t3 ON t1.ID_=t3.PROC_INST_ID_
and t1.START_USER_ID_ = #{userCode} t3.SUSPENSION_STATE_ !=2 and t3.SUSPENSION_STATE_ =2 AND t1.START_TIME_ >= #{beginTime} AND t1.START_TIME_ <=#{endTime} AND t1.START_TIME_ < #{lastCreateTime}
order by t1.START_TIME_ desc
